    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{# On crée des variables personnalisées #}
{% set queryParams = app.request.query.all %}

{% block title %}Liste des annonces{% endblock %}


{% block body %}
    <div class=\"container\">
        <div class=\"grix xs1 sm5 gutter-0 card\">
            <div class=\"col-xs1\">

                <p>Filtrer</p>
                <form id=\"filters\">
                    {% for categorie in categories %}
                        {# On vérifie si la catégorie est dans les paramètres #}
                        {% if queryParams is not empty and queryParams.categories is defined %}
                            {% set checked = (categorie.id in queryParams.categories) ? \"checked\" : \"\" %}
                        {% else %}
                            {% set checked = \"\" %}
                        {% endif %}

                        <p><input type=\"checkbox\" name=\"categories[]\" id=\"cat{{categorie.id}}\" value=\"{{categorie.id}}\" {{checked}}> <label for=\"cat{{categorie.id}}\">{{categorie.Libelle}}</label></p>
                    {% endfor %}
                    {% include \"produit/produitajax.html.twig\" %}
                </form>
            </div>
            <div class=\"col-xs1 col-sm4\" id=\"content\">

            </div>
        </div>
    </div>
{% endblock %}

{% block javascripts %}
    <script src=\"{{ asset('js/filters.js') }}\"></script>
{% endblock %}", "produit/indexajax.html.twig", "C:\\Users\\Nour Hammami\\Desktop\\pii\\PiDev10\\templates\\produit\\indexajax.html.twig");
